import React, {Component} from 'react';

class Counter extends Component {
  // 컴포넌트 내부에서 선언되는 값
  state = {
    number : 0
  }

  //state 값을 조작하는 함수 1
  handleIncrease = () => {
    this.setState({
      number: this.state.number + 1
    });
  }

  //state 값을 조작하는 함수 2
  handleDecrease = () => {
    this.setState({
      number: this.state.number - 1
    });
  }

  render(){
    return(
      <div>
        <h1>카운터</h1>
        <div>값: {this.state.number}</div>
        <button onClick={this.handleIncrease}>+</button>
        <button onClick={this.handleDecrease}>-extends</button>
      </div>
    );
  }
}


export default Counter;
