import React,  { Component } from 'react';

class MyName extends Component {
  // props 기본 값, 부모 컴포넌트에서 특정 상황에 props값을 내려주지 않을 경우 defaultProps값으로 대체될 수 있습니다
  static defaultProps = {
    name: '기본 이름'
  }
  render(){
    return(
      //props는 부모 컴포넌트에서 받아오는 값입니다 -- 자식 컴포넌트에서 조작 불가
      <div>
        안녕하세요! 제 이름은 <b>{this.props.name}</b>입니다.
      </div>
    );
  }
}

export default MyName;
